import "./App.css";
import { useState } from "react";
import Header from "./components/header/Header";
import {
  Routes,
  BrowserRouter as Browser,
  Route,
  Link,
} from "react-router-dom";
import LandingPage from "./components/LandingPage/LandingPage";
import Overview from "./components/overview/overview";
import Education from "./components/education/Education";
import ConsultingInvestment from "./components/ConsultingInvestment/ConsultingInvestment";
import SignUp from "./components/SignUp/SignUp";
import Login from "./components/login/login";
import Founder from "./components/founder/Founder";
import Footer from "./components/footer/Footer";
import Prices from "./components/prices/Prices";

function App() {
  const [toggle, setToggle] = useState(true);

  return (
    <>
      <Browser>
      <div className="overflow-hidden relative w-[100vw] h-[100vh] bg-red-500">
        <div style={{transition: "right 0.8s ease"}} className={`absolute top-0 bottom-0 bg-green-400 ${toggle === true? "sm:right-5 right-0": "right-[-100%]"}`}>
          {/* <NavBar toggle={toggle} setToggle={setToggle} /> */}
        </div>
        <div className="h-full overflow-auto">
        <Routes>

        </Routes>
        <Prices/>
        <Footer/>   
        </div>
      </div>
      </Browser> 
    </>
  );
}
function NavBar({ toggle, setToggle }) {
  return (
    <div
      style={{
        cursor:"pointer",
        boxShadow: " 0 0 3px 3px #133d59",
        backgroundColor: "#133d59",
      }}
      onClick={()=>{
        setToggle(false)
      }}
      className="flex items-center  font-semibold text-sm  p-10 gap-3 justify-center flex-col"  
    >
      <Link
        className="text-white hover:text-amber-300 text-xl"
        to="/"
        onClick={() => {
          setToggle(false);
        }}
      >
        Home <br />
      </Link>
      <Link
        className="text-white hover:text-[#f2b31b] text-xl"
        to="/signup"
        onClick={() => {
          setToggle(false);
        }}
      >
        Sign Up
      </Link>
      <Link
        className="text-white hover:text-[#f2b31b] text-xl"
        to="/login"
        onClick={() => {
          setToggle(false);
        }}
      >
        Login
      </Link>
      <Link
        className="text-white hover:text-[#f2b31b] text-xl"
        to="/consultingandinvestment"
        onClick={() => {
          setToggle(false);
        }}
      >
        Consulting And Investment
      </Link>
      <Link
        className="text-white hover:text-[#f2b31b] text-xl"
        to="/companyoverview"
        onClick={() => {
          setToggle(false);
        }}
      >
        Company Overview
      </Link>
      <Link
        className="text-white hover:text-[#f2b31b] text-xl"
        to="/educationcourse"
        onClick={() => {
          setToggle(false);
        }}
      >
        Education Courses
      </Link>
      <Link
        className="text-white hover:text-[#f2b31b] text-xl"
        to="/foundingdetails"
        onClick={() => {
          setToggle(false);
        }}
      >
        Founding Details
      </Link>
    </div>
  );
}

export default App;
