import "./App.css";
import { useState } from "react";
import Header from "./components/header/Header";
import Slider from "./components/slider/Slider";
import {
  Navigate,
  Routes,
  BrowserRouter as Router,
  Route,
  Link
} from "react-router-dom";

// import Projects from './components/projects/Projects';
// // import ChoseUs from './components/choseUs/ChoseUs';
// // import Work from './components/work/Work';
// // import How from './components/how/How';
// // import Feature from './components/feature/Feature';
// // import Token from './components/token/Token';
// // import Arts from './components/Linkrts/Linkrts';
// //  import Faq from './components/faq/Faq';
// // import Prices from './components/prices/Prices';
//    import Footer from './components/footer/Footer';
import LandingPage from "./components/LandingPage/LandingPage";
import Overview from "./components/overview/overview";
import Education from "./components/education/Education";
import ConsultingInvestment from "./components/ConsultingInvestment/ConsultingInvestment";
import ContactUs from "./components/ContactUs/ContactUs";
import SignUp from "./components/SignUp/SignUp";
import Login from "./components/login/login";
import Founder from "./components/founder/Founder";
import Footer from "./components/footer/Footer"
import Prices from "./components/prices/Prices";
// import Aside from "./components/sidebar/Linkside";

function App() {
  const [toggle, setToggle] = useState(false);

  return (
    <>
      <Router>
    <div className="">
          <div className="hero">
            <Header toggle={toggle} setToggle={setToggle} />
            <NavBar toggle={toggle} />
          </div>


        <Routes>
        <Route exact path="/" element={<LandingPage />} />

          <Route exact path="/companyoverview" element={<Overview />} />
          <Route exact path="/signup" element={<SignUp />} />
          <Route exact path="/login" element={<Login />} />

          <Route
            exact path="/consultingandinvestment"
            element={<ConsultingInvestment />}
          />
          <Route exact path="/educationcourse" element={<Education />} />
          <Route exact path="/foundingdetails" element={<Founder />} />
        </Routes>


      <Prices />
        <Footer />

        </div>
        </Router>
    </>
  );
}
function NavBar({toggle}) {

  return (
    <div style={{boxShadow : " 0 0 5px 5px #133d59", backgroundColor: "#133d59", transition:"margin-right 0.5s ease"}} className={`flex items-center absolute z-10 right-0 bottom-0   top-0 font-semibold text-sm  p-10 gap-3  transition:m justify-center flex-col ${toggle === true? "ease-linear" : " hidden"} `}>
      <Link className="text-white hover:text-amber-300 text-3xl" to="/">
        Home <br />
      </Link>
      <Link className="text-white hover:text-[#f2b31b] text-3xl" to="/signup">Sign Up</Link>
      <Link className="text-white hover:text-[#f2b31b] text-3xl" to="/login">Login</Link>
      <Link className="text-white hover:text-[#f2b31b] text-3xl" to="/consultingandinvestment">Consulting And Investment</Link>
      <Link className="text-white hover:text-[#f2b31b] text-3xl" to="/companyoverview">Comapny Overview</Link>
      <Link className="text-white hover:text-[#f2b31b] text-3xl" to="/educationcourse">Education Course</Link>
      <Link className="text-white hover:text-[#f2b31b] text-3xl" to="/foundingdetails">Founding Details</Link>
    </div>
  )
}

export default App;
