import "./App.css";
import { useState } from "react";
import Header from "./components/header/Header";
import Slider from "./components/slider/Slider";
import {
  Navigate,
  Routes,
  BrowserRouter as Browser,
  Route,
  Link,
} from "react-router-dom";
import LandingPage from "./components/LandingPage/LandingPage";
import Overview from "./components/overview/overview";
import Education from "./components/education/Education";
import ConsultingInvestment from "./components/ConsultingInvestment/ConsultingInvestment";
import ContactUs from "./components/ContactUs/ContactUs";
import SignUp from "./components/SignUp/SignUp";
import Login from "./components/login/login";
import Founder from "./components/founder/Founder";
import Footer from "./components/footer/Footer";
import Prices from "./components/prices/Prices";
// import Aside from "./components/sidebar/Linkside";

function App() {
  const [toggle, setToggle] = useState(false);

  return (
    <>
      <Browser>
      <Header toggle={toggle} setToggle={setToggle}/>
      <NavBar toggle={toggle} setToggle={setToggle} />
      <Routes>
        <Route path="/" element={<LandingPage />} />

          <Route path="/companyoverview" element={<Overview />} />
          <Route path="/signup" element={<SignUp />} />
          <Route path="/login" element={<Login />} />

          <Route
            path="/consultingandinvestment"
            element={<ConsultingInvestment />}
          />
          <Route path="/educationcourse" element={<Education />} />
          <Route path="/foundingdetails" element={<Founder />} />
        </Routes>
        <Prices/>
        <Footer/>
      </Browser>
    </>
  );
}
function NavBar({ toggle, setToggle }) {
  return (
    <div
      style={{
        cursor:"pointer",
        boxShadow: " 0 0 5px 5px #133d59",
        backgroundColor: "#133d59",
        transition: "margin-right 0.5s ease",
        position:"sticky"
      }}
      onClick={()=>{
        setToggle(false)
      }}
      className={`flex items-center sticky z-10 right-0 bottom-0 top-0 font-semibold text-sm  p-10 gap-3  transition:m justify-center flex-col ${
        toggle === true?"ease-linear" : " hidden"
      } `}  
    >
      <Link
        className="text-white hover:text-amber-300 text-xl"
        to="/"
        onClick={() => {
          setToggle(false);
        }}
      >
        Home <br />
      </Link>
      <Link
        className="text-white hover:text-[#f2b31b] text-xl"
        to="/signup"
        onClick={() => {
          setToggle(false);
        }}
      >
        Sign Up
      </Link>
      <Link
        className="text-white hover:text-[#f2b31b] text-xl"
        to="/login"
        onClick={() => {
          setToggle(false);
        }}
      >
        Login
      </Link>
      <Link
        className="text-white hover:text-[#f2b31b] text-xl"
        to="/consultingandinvestment"
        onClick={() => {
          setToggle(false);
        }}
      >
        Consulting And Investment
      </Link>
      <Link
        className="text-white hover:text-[#f2b31b] text-xl"
        to="/companyoverview"
        onClick={() => {
          setToggle(false);
        }}
      >
        Company Overview
      </Link>
      <Link
        className="text-white hover:text-[#f2b31b] text-xl"
        to="/educationcourse"
        onClick={() => {
          setToggle(false);
        }}
      >
        Education Courses
      </Link>
      <Link
        className="text-white hover:text-[#f2b31b] text-xl"
        to="/foundingdetails"
        onClick={() => {
          setToggle(false);
        }}
      >
        Founding Details
      </Link>
    </div>
  );
}

export default App;
